import React from "react";
import SearchBar from "./SearchBar";
import MovieList from "./MovieList";
import axios from "axios";

class App extends React.Component {
    state = {
        movies: [],
        searchQuery: ""
    }

    /* ---------FETCH API ile işlemler--------- */
    /* async componentDidMount() {
        const baseURL = "http://localhost:3002/movies";  Fake Rest API kaynağım 
        const response = await fetch(baseURL);
        const data = await response.json();
        this.setState({ movies: data });
    } */

    /* deleteMovie = async (movie) => {
        const baseURL = `http://localhost:3002/movies/${movie.id}`;
        await fetch(baseURL, {
            method: "DELETE"
        })
        const newMovieList = this.state.movies.filter(
            m => m.id !== movie.id
        );

        this.setState({
            movies: newMovieList
        })
    } */
    /* ---------FETCH API ile işlemler--------- */


    async componentDidMount() {
        const response = await axios.get("http://localhost:3002/movies");
        this.setState({ movies: response.data });
    }

    /* deleteMovie = (movie) => {
        const newMovieList = this.state.movies.filter(
             Silinen id ile diziyi filtreliyorum 
            m => m.id !== movie.id
        );

        this.setState({
             Filtrelediğim diziyi setState ile güncelliyorum 
            movies: newMovieList
        })
    } */

    searchMovie = (event) => {
        this.setState({ searchQuery: event.target.value })
    }

    render() {
        let filteredMovies = this.state.movies.filter(
            (movie) => {
                /* Burada searchQuery değerini diziden filtreliyorum ve geri döndürüyorum */
                return movie.name.toLowerCase().indexOf(this.state.searchQuery.toLowerCase()) !== -1
            }
        )

        return (
            <div className="container">
                <div className="row">
                    <div className="col-lg-12">
                        <SearchBar searchMovieProp={this.searchMovie} />
                    </div>
                </div>
                <MovieList
                    movies={filteredMovies}
                    deleteMovieProp={this.deleteMovie}
                />
            </div>
        )
    }
}

export default App;
